@auth_api.route('/api/check-code')
def check_code():
    """
    ---
    get:
      summary: Проверить код подтверждения
      parameters:
          - in: query
            name: code
            schema:
              type: string
              example: 1234
            description: SMS код
          - in: query
            name: phone
            schema:
              type: string
              example: 79151290127
            description: Номер телефона
          - in: query
            name: OS
            schema:
              type: string
              example: Android
            description: Операционная система
          - in: query
            name: DeviceID
            schema:
              type: string
              example: Gnx786nzdg758
            description: DeviceID
      responses:
        '200':
          description: Результат
          content:
            application/json:
              schema:      # Request body contents
                  type: object
                  properties:
                    status:
                        type: bool
                    user:
                        type: string
                  example:   # Sample object
                    status: true
                    user: 5zqxZa16b0vEE1sx$9a74b2452862f8b0061a5356079f69c3b83af9aec7430d070901f745b984a3f9

        '403':
          description: Пользователь заблокирован
          content:
            application/json:
              schema: ErrorSchema

        '401':
          description: Неверный код
          content:
            application/json:
              schema: ErrorSchema
        '400':
          description: Не передан обязательный параметр
          content:
            application/json:
              schema: ErrorSchema
      tags:
        - auth
    """
    try:
        phone = request.args.get('phone')
        if User.query.filter_by(phone=phone).first():
            user = User.query.filter_by(phone=phone).first()
            if User.query.filter_by(phone=phone).first().status == "blocked":
                return current_app.response_class(
                    response=json.dumps(
                        {'error': "USER BLOCKED"}
                    ),
                    status=403,
                    mimetype='application/json'
                )
            else:
                deviceId = request.args.get('DeviceId')
                os = request.args.get('os')
                action(user.token, deviceId, os)
            token = user.token
        else:
            token = None
        code = request.args.get('code')
        if Codes.query.filter_by(phone=phone).first():
            if token:
                if datetime.datetime.fromtimestamp(time.time()).day > datetime.datetime.fromtimestamp(
                        user.last_updated).day:
                    new_points = Points(user_id=User.query.filter_by(phone=phone).first().id, side=1, quantity=1,
                                        type=3, timestamp=time.time())
                    points = User.query.filter_by(phone=phone).first().points
                    if not points:
                        points = 0
                    User.query.filter_by(phone=phone).update({'points': float(points) + 1})
                    db.session.add(new_points)
                    db.session.commit()
            if Codes.query.filter_by(phone=phone).all()[-1].code == code:
                return current_app.response_class(
                    response=json.dumps(
                        {'status': True,
                         'user': token}
                    ),
                    status=200,
                    mimetype='application/json'
                )
            else:
                return current_app.response_class(
                    response=json.dumps(
                        {'status': False,
                         'user': None}
                    ),
                    status=200,
                    mimetype='application/json'
                )
        else:
            return current_app.response_class(
                response=json.dumps(
                    {'error': "Invalid code"}
                ),
                status=401,
                mimetype='application/json'
            )
    except Exception as e:
        return current_app.response_class(
            response=json.dumps(
                {'error': f'ERROR: {e}!'}
            ),
            status=400,
            mimetype='application/json'
        )


@auth_api.route('/api/get-jk')
def get_jk():
    """
    ---
    get:
      summary: Получить информацию о ЖК по адресу
      parameters:
          - in: query
            name: address
            schema:
              description: Адрес
              type: string
              example: 108804, г Москва, поселение Кокошкино, Новомосковский округ, дп Кокошкино, ул Пушкина, д 5
            description: Адрес
      responses:
        '200':
          description: Результат
          content:
            application/json:
              schema:      # Request body contents
                  type: object
                  properties:
                    jk_id:
                        type: integer
                    jk_name:
                        type: string
                  example:   # Sample object
                    jk_id: 1
                    jk_name: "ЖК Паруса"
        '404':
          description: ЖК не найден
          content:
            application/json:
              schema: ErrorSchema
        '400':
          description: Не передан обязательный параметр
          content:
            application/json:
              schema: ErrorSchema
      tags:
        - auth
    """
    try:
        address = request.args.get('address')
        jk = Addresses.query.filter_by(name=address).first()
        if jk:
            jk = JK.query.filter_by(id=jk.jk_id).first()
            return current_app.response_class(
                response=json.dumps(
                    {'jk_id': jk.id,
                     'jk_name': jk.name}
                ),
                status=200,
                mimetype='application/json'
            )
        else:
            return current_app.response_class(
                response=json.dumps(
                    {'error': f'ЖК не найден'}
                ),
                status=404,
                mimetype='application/json'
            )
    except Exception as e:
        return current_app.response_class(
            response=json.dumps(
                {'error': f'ERROR: {e}!'}
            ),
            status=400,
            mimetype='application/json'
        )


@auth_api.route('/api/get-addresses')
def get_addresses():
    """
    ---
    get:
      summary: Получить информацию о ЖК по адресу
      parameters:
          - in: query
            name: address
            schema:
              description: Адрес
              type: string
              example: Россия, г. Москва, ул. Госпитальный вал, д. 3
            description: Адрес
      responses:
        '200':
          description: Результат
          content:
            application/json:
              schema:      # Request body contents
                  type: object
                  properties:
                    list:
                        type: array
                        items:
                            name:
                                type: string
        '404':
          description: ЖК не найден
          content:
            application/json:
              schema: ErrorSchema
        '400':
          description: Не передан обязательный параметр
          content:
            application/json:
              schema: ErrorSchema
      tags:
        - auth
    """
    try:
        address = request.args.get('address')
        dadata = Dadata(DADATA)
        result = dadata.suggest("address", address, 10)
        addresses = []
        for i in result:
            addresses.append({
                'name': i['unrestricted_value']
            })
        return current_app.response_class(
            response=json.dumps(
                {'list': addresses}
            ),
            status=200,
            mimetype='application/json'
        )
    except Exception as e:
        return current_app.response_class(
            response=json.dumps(
                {'error': f'ERROR: {e}!'}
            ),
            status=400,
            mimetype='application/json'
        )


@auth_api.route('/api/sign_up', methods=['GET', 'POST'])
def sign_up():
    """
    ---
    post:
      summary: Регистрация
      parameters:
          - in: query
            name: org
            schema:
              description: организация или жилец(1 если организация, 0 если жилец)
              type: integer
              example: 1
            description: организация или жилец(1 если организация, 0 если жилец)
          - in: query
            name: phone
            schema:
              description: номер телефона
              type: string
              example: 79151290127
            description: номер телефона
          - in: query
            name: OS
            schema:
              type: string
              example: Android
            description: Операционная система
          - in: query
            name: DeviceID
            schema:
              type: string
              example: Gnx786nzdg758
            description: DeviceID
      requestBody:
        content:
          application/json:
              schema:
                type: object
                properties:
                    name:
                        type: string
                    surname:
                        type: string
                    second_name:
                        type: string
                    address:
                        type: string
                    jk:
                        type: string
                    jk_exist:
                        type: integer
                    inn:
                        type: string
                    org_name:
                        type: string
                    is_uk:
                        type: integer
                example:   # Sample object
                    name: Иван
                    surname: Иванов
                    second_name: Иванович
                    address: 108804, г Москва, поселение Кокошкино, Новомосковский округ, дп Кокошкино, ул Пушкина, д 5
                    jk_exist: 1
                    jk: 1
                    inn: 86869878978
                    org_name: ООО Ромашка
                    is_uk: 0
      responses:
        '200':
          description: Результат
          content:
            application/json:
              schema:      # Request body contents
                  type: object
                  properties:
                    token:
                        type: string
                  example:   # Sample object
                    token: 5zqxZa16b0vEE1sx$9a74b2452862f8b0061a5356079f69c3b83af9aec7430d070901f745b984a3f9
        '403':
          description: Пользователь с таким телефоном уже существует
          content:
            application/json:
              schema: ErrorSchema
        '400':
          description: Не передан обязательный параметр
          content:
            application/json:
              schema: ErrorSchema
      tags:
        - auth
    """
    try:
        phone = request.args.get('phone')
        name = request.json.get('name')
        surname = request.json.get('surname')
        org = int(request.args.get('org'))
        address = request.json.get('address')
        jk_exist = int(request.json.get('jk_exist'))
        jk = request.json.get('jk')
        if org:
            second_name = request.json.get('second_name')
            inn = request.json.get('inn')
            org_name = request.json.get('org_name')
            is_uk = request.json.get('is_uk')
        if User.query.filter_by(phone=phone).first():
            return current_app.response_class(
                response=json.dumps(
                    {'error': f'USER EXIST'}
                ),
                status=403,
                mimetype='application/json'
            )
        if not jk_exist:
            dadata = Dadata(DADATA)
            result = dadata.suggest("address", address, 10)[0]['data']
            new_jk = JK(name=request.json.get('jk'), city=result['city'], moderated=0)
            db.session.add(new_jk)
            db.session.commit()
            jk = new_jk.id
            new_address = Addresses(name=address, city=result['city'], street=result['street'], jk_id=jk)
            db.session.add(new_address)
            db.session.commit()
        token = generate_password_hash(phone, method='sha256').replace('sha256$', '')
        if org:
            if is_uk:
                new_user = User(phone=phone, name=name, surname=surname, second_name=second_name, org=1, address=address,
                                inn=inn, org_name=org_name, status="active", token=token, jk=jk, points=10, is_uk=is_uk,
                                registered=int(time.time()), photo='default_uk.png')
            else:
                new_user = User(phone=phone, name=name, surname=surname, second_name=second_name, org=1, address=address,
                                inn=inn, org_name=org_name, status="active", token=token, jk=jk, points=10, is_uk=is_uk,
                                registered=int(time.time()), photo='default_org.png')
            db.session.add(new_user)
            db.session.commit()
        else:
            new_user = User(phone=phone, name=name, surname=surname, org=1, address=address,
                            status="active", token=token, jk=jk, points=10, registered=int(time.time()))
            db.session.add(new_user)
            db.session.commit()
        deviceId = request.args.get('DeviceID')
        os = request.args.get('OS')
        action(token, deviceId, os)
        new_points = Points(user_id=new_user.id, side=1, quantity=10, type=1, timestamp=time.time())
        db.session.add(new_points)
        db.session.commit()
        return current_app.response_class(
            response=json.dumps(
                {'token': token}
            ),
            status=200,
            mimetype='application/json'
        )
    except Exception as e:
        return current_app.response_class(
            response=json.dumps(
                {'error': f'ERROR: {e}!'}
            ),
            status=400,
            mimetype='application/json'
        )


@auth_api.route('/api/user-blocked', methods=['GET', 'POST'])
def user_blocked():
    """
    ---
    get:
      summary: Проверка не заблокирован ли пользователь
      parameters:
          - in: query
            name: phone
            schema:
              description: номер телефона
              type: string
              example: 79151290127
            description: номер телефона
      responses:
        '200':
          description: Результат
          content:
            application/json:
              schema:      # Request body contents
                  type: object
                  properties:
                    blocked:
                        type: string
                  example:   # Sample object
                    blocked: 0
        '403':
          description: Пользователь с таким телефоном уже существует
          content:
            application/json:
              schema: ErrorSchema
        '400':
          description: Не передан обязательный параметр
          content:
            application/json:
              schema: ErrorSchema
      tags:
        - auth
    """
    try:
        phone = request.args.get('phone')
        if not User.query.filter_by(phone=phone).first():
            return current_app.response_class(
                response=json.dumps(
                    {'error': f'USER NOT EXIST'}
                ),
                status=403,
                mimetype='application/json'
            )
        return current_app.response_class(
            response=json.dumps(
                {'blocked': 1 if User.query.filter_by(phone=phone).first().status == 'blocked' else 0}
            ),
            status=200,
            mimetype='application/json'
        )
    except Exception as e:
        return current_app.response_class(
            response=json.dumps(
                {'error': f'ERROR: {e}!'}
            ),
            status=400,
            mimetype='application/json'
        )


@auth_api.route('/api/referer')
def referer():
    """
    ---
    get:
      summary: Ввести код пригласившего
      parameters:
          - in: query
            name: phone
            schema:
              description: Phone number
              type: string
              example: 79151290127
            description: Номер телефона
          - in: query
            name: token
            schema:
              description: Токен
              type: string
              example: 5zqxZa16b0vEE1sx$9a74b2452862f8b0061a5356079f69c3b83af9aec7430d070901f745b984a3f9
            description: Токен
      responses:
        '200':
          description: Результат
          content:
            application/json:
              schema:      # Request body contents
                  type: object
                  properties:
                    result:
                      type: string
                    status:
                        type: string
                  example:   # Sample object
                    result: Сообщение отправлено
                    status: ok
        '401':
          description: Неверный токен
          content:
            application/json:
              schema: ErrorSchema
        '404':
          description: Нет такого номера телефона в базе
          content:
            application/json:
              schema: ErrorSchema
        '400':
          description: Не передан обязательный параметр
          content:
            application/json:
              schema: ErrorSchema
      tags:
        - auth
    """
    try:
        phone = request.args.get('phone')
        token = request.args.get('token')
        if not User.query.filter_by(token=token).first():
            return current_app.response_class(
                response=json.dumps(
                    {'result': 'User not found',
                     'status': 'error'}
                ),
                status=401,
                mimetype='application/json'
            )
        new_points = Points(user_id=User.query.filter_by(phone=phone).first().id, side=1, quantity=5, type=2,
                            timestamp=time.time())
        if User.query.filter_by(phone=phone).first() and not User.query.filter_by(token=token).first().referer:
            points = User.query.filter_by(phone=phone).first().points
            _ = User.query.filter_by(token=token).update({'referer': User.query.filter_by(phone=phone).first().id})
            if not points:
                points = 0
            User.query.filter_by(phone=phone).update({'points': float(points) + 5})
            db.session.add(new_points)
            db.session.commit()
            sender = Gate(SMS_LOGIN, SMS_PASSWORD)
            status = sender.send_message(phone, f'Поздравляем, вам начислен бонус - 5 баллов за приглашенного друга!',
                                         'SMS DUCKOHT')
            if status.split(';')[0] == 'accepted':
                return current_app.response_class(
                    response=json.dumps(
                        {'result': status,
                         'status': 'ok'}
                    ),
                    status=200,
                    mimetype='application/json'
                )
            else:
                return current_app.response_class(
                    response=json.dumps(
                        {'result': status,
                         'status': 'error'}
                    ),
                    status=400,
                    mimetype='application/json'
                )
        else:
            return current_app.response_class(
                response=json.dumps(
                    {'result': 'Пользователь не найден',
                     'status': 'error'}
                ),
                status=404,
                mimetype='application/json'
            )
    except Exception as e:
        return current_app.response_class(
            response=json.dumps(
                {'error': f'ERROR: {e}!'}
            ),
            status=400,
            mimetype='application/json'
        )


