@auth_api.route('/api/edit_profile', methods=['POST'])
def edit_profile():
    """
    ---
    post:
      summary: Редактировать профиль
      parameters:
          - in: query
            name: token
            schema:
              type: string
              example: 5zqxZa16b0vEE1sx$9a74b2452862f8b0061a5356079f69c3b83af9aec7430d070901f745b984a3f9
            description: токен
          - in: query
            name: OS
            schema:
              type: string
              example: Android
            description: Операционная система
          - in: query
            name: DeviceID
            schema:
              type: string
              example: Gnx786nzdg758
            description: DeviceID
      requestBody:
        required: true
        content:
          application/json:
              schema:
                type: object
                properties:
                    photo:
                        type: string
                        description: Фото Base64
                    name:
                        type: string
                        description: Имя
                    surname:
                        type: string
                        description: Фамилия
                    second_name:
                        type: string
                        description: Отчество
                    address:
                        type: string
                        description: Адрес
                    jk:
                        type: string
                        description: ID ЖК
                    jk_exist:
                        type: integer
                        description: Существует ли такой ЖК в базе
                    inn:
                        type: string
                        description: ИНН
                    org_name:
                        type: string
                        description: Наименование организации
                    is_uk:
                        type: integer
                        description: Является ли УК
                example:   # Sample object
                    photo: 
                    name: Иван
                    surname: Иванов
                    second_name: Иванович
                    status: active
                    address: 108804, г Москва, поселение Кокошкино, Новомосковский округ, дп Кокошкино, ул Пушкина, д 5
                    jk_exist: 1
                    jk: 1
                    inn: 86869878978
                    org_name: ООО Ромашка
                    is_uk: 0
      responses:
        '200':
          description: Результат
          content:
            application/json:
              schema:      # Request body contents
                  type: object
                  properties:
                    status:
                        type: string
                  example:   # Sample object
                    status: ok
        '401':
          description: Неверный токен
          content:
            application/json:
              schema: ErrorSchema
        '400':
          description: Не передан обязательный параметр
          content:
            application/json:
              schema: ErrorSchema
      tags:
        - auth
    """
    try:
        token = request.args.get('token')
        user = User.query.filter_by(token=token).first()
        photo = request.json.get('photo')
        name = request.json.get('name')
        surname = request.json.get('surname')
        address = request.json.get('address')
        jk_exist = int(request.json.get('jk_exist'))
        jk = request.json.get('jk')
        if not user:
            return current_app.response_class(
                response=json.dumps(
                    {'error': f'Incorrect TOKEN'}
                ),
                status=401,
                mimetype='application/json'
            )
        if user.org:
            second_name = request.json.get('second_name')
            inn = request.json.get('inn')
            org_name = request.json.get('org_name')
            is_uk = request.json.get('is_uk')

        if photo:
            im = Image.open(BytesIO(base64.b64decode(photo)))
            im.save(f"{getcwd()}/app/static/profile_photos/{user.id}.png")
            _ = User.query.filter_by(token=token).update(
                {'photo': f'{user.id}.png'})
            db.session.commit()
        if User.query.filter_by(token=token).first().status == "blocked":
            return current_app.response_class(
                response=json.dumps(
                    {'error': "USER BLOCKED"}
                ),
                status=403,
                mimetype='application/json'
            )
        deviceId = request.args.get('DeviceId')
        os = request.args.get('os')
        action(token, deviceId, os)
        if not jk_exist:
            new_jk = JK(name=jk)
            db.session.add(new_jk)
            db.session.commit()
            jk = new_jk.id
        if user.org:
            _ = User.query.filter_by(id=user.id).update({'name': name, 'surname': surname, 'second_name': second_name,
                                                         'address': address, 'inn': inn, 'org_name': org_name,
                                                         'jk': jk, 'is_uk': is_uk})
        else:
            _ = User.query.filter_by(id=user.id).update(
                {'name': name, 'surname': surname, 'address': address, 'jk': jk})
        db.session.commit()
        return current_app.response_class(
            response=json.dumps(
                {'status': f'ok'}
            ),
            status=200,
            mimetype='application/json'
        )
    except Exception as e:
        return current_app.response_class(
            response=json.dumps(
                {'error': f'ERROR: {e}!'}
            ),
            status=400,
            mimetype='application/json'
        )


@auth_api.route('/api/points')
def points():
    """
    ---
    get:
      summary: Возможности заработка баллов
      parameters:
          - in: query
            name: token
            schema:
              description: Токен
              type: string
              example: 5zqxZa16b0vEE1sx$9a74b2452862f8b0061a5356079f69c3b83af9aec7430d070901f745b984a3f9
            description: Токен
          - in: query
            name: OS
            schema:
              type: string
              example: Android
            description: Операционная система
          - in: query
            name: DeviceID
            schema:
              type: string
              example: Gnx786nzdg758
            description: DeviceID
      responses:
        '200':
          description: Результат
          content:
            application/json:
              schema:      # Request body contents
                  type: object
                  properties:
                    list:
                      type: array
                      items:
                          type: object
                          properties:
                            id:
                              type: integer
                            quantity:
                              type: number
                            name:
                              type: string
                            description:
                              type: string
                            achived:
                              type: integer
        '403':
          description: Пользователь заблокирован
          content:
            application/json:
              schema: ErrorSchema
        '401':
          description: Не верный токен
          content:
            application/json:
              schema: ErrorSchema
        '400':
          description: Не передан обязательный параметр
          content:
            application/json:
              schema: ErrorSchema
      tags:
        - auth
    """
    try:
        token = request.args.get('token')
        user = User.query.filter_by(token=token).first()
        if not user:
            return current_app.response_class(
                response=json.dumps(
                    {'error': f'USER DOES NOT EXIST'}
                ),
                status=403,
                mimetype='application/json'
            )
        if user.status == "blocked":
            return current_app.response_class(
                response=json.dumps(
                    {'error': "USER BLOCKED"}
                ),
                status=403,
                mimetype='application/json'
            )
        deviceId = request.args.get('DeviceId')
        os = request.args.get('os')
        action(token, deviceId, os)
        array = []
        points = PointsTypes.query.filter_by(side=1).all()
        for i in points:
            array.append({
                "id": i.id,
                "name": i.name,
                "quantity": i.quantity,
                "description": i.description,
                "achieved": 1 if Points.query.filter_by(user_id=user.id, type=i.id).first() else 0
            })
        return current_app.response_class(
            response=json.dumps(
                {
                    "list": array
                }
            ),
            status=200,
            mimetype='application/json'
        )
    except Exception as e:
        return current_app.response_class(
            response=json.dumps(
                {'error': f'ERROR: {e}!'}
            ),
            status=400,
            mimetype='application/json'
        )


